mapper.select:
SELECT s.sku_id as skuId, p.cover_img as converpic, p.designer_id as designerId, p.color_code as colorCode, s.price as retailPrice, s.in_price as boutiquePrice, s.size as size, CONCAT_WS("->" , c2. NAME , c1. NAME , c. NAME) categoryName , b.english_name as brandName , p.season_code as seasonCode, ss.store as stock FROM sku s LEFT JOIN sku_store ss ON(ss.sku_id = s.sku_id) LEFT JOIN product p ON(p.product_id = s.product_id) LEFT JOIN brand b ON(b.brand_id = p.brand_id) LEFT JOIN category c ON(c.category_id = p.category_id) LEFT JOIN category c1 ON(c1.category_id = c.parent_id) LEFT JOIN category c2 ON(c2.category_id = c1.parent_id) WHERE p.enabled = 1
and p.vendor_id = #{vendorId,jdbcType=BIGINT}
and p.designer_id = #{designerId,jdbcType=VARCHAR}
and p.color_code = #{colorCode,jdbcType=VARCHAR}
and p.brand_id = #{brandId,jdbcType=BIGINT}
and p.category_id = #{categoryId,jdbcType=BIGINT}
and p.season_code = #{seasonCode,jdbcType=VARCHAR}
ORDER BY ss.last_check desc LIMIT #{start},#{pageSize};

SELECT
 count(*)
FROM
sku s
LEFT JOIN sku_store ss ON(ss.sku_id = s.sku_id)
LEFT JOIN product p ON(p.product_id = s.product_id)
LEFT JOIN brand b ON(b.brand_id = p.brand_id)
LEFT JOIN category c ON(c.category_id = p.category_id)
LEFT JOIN category c1 ON(c1.category_id = c.parent_id)
LEFT JOIN category c2 ON(c2.category_id = c1.parent_id)
WHERE p.enabled = 1

    and p.vendor_id =  #{vendorId,jdbcType=BIGINT}


    and p.designer_id =  #{designerId,jdbcType=VARCHAR}


    and p.color_code =  #{colorCode,jdbcType=VARCHAR}


    and p.brand_id =  #{brandId,jdbcType=BIGINT}


    and p.category_id =  #{categoryId,jdbcType=BIGINT}


    and p.season_code =  #{seasonCode,jdbcType=VARCHAR}
