SELECT
tag.tag_id,
tag.tag_name
FROM tag
LEFT JOIN block_tag_rel ON tag.tag_id = block_tag_rel.tag_id
WHERE tag.enabled = TRUE AND (block_tag_rel.block_tag_id IS NULL

    OR block_tag_rel.block_id = #{blockId}

);

SELECT tag.tag_id, tag_product_rel.product_id, tag.tag_name FROM tag INNER JOIN tag_product_rel ON tag.tag_id = tag_product_rel.tag_id WHERE tag_product_rel.product_id IN
#{item.product_id}
AND tag.enabled = TRUE;

SELECT block.block_id, block.block_name, block.modified_at, block.status, block.sort_order, block.title, block.subtitle, tag.tag_id, tag.tag_name FROM block LEFT JOIN block_tag_rel ON block.block_id = block_tag_rel.block_id LEFT JOIN tag ON tag.tag_id = block_tag_rel.tag_id AND tag.enabled = TRUE WHERE block.enabled = TRUE
AND block.block_name LIKE "%"#{blockName}"%"
AND block.status = #{status}
AND tag.tag_id IS NULL
AND tag.tag_id = #{tagId}
AND unix_timestamp(block.modified_at) BETWEEN #{modifiedAtFrom} AND #{modifiedAtTo}
ORDER BY block.sort_order
DESC
LIMIT #{start}, #{limit};

SELECT count(block.block_id) as blockSize FROM block LEFT JOIN block_tag_rel ON block.block_id = block_tag_rel.block_id LEFT JOIN tag ON tag.tag_id = block_tag_rel.tag_id AND tag.enabled = TRUE WHERE block.enabled = TRUE
AND block.block_name LIKE "%"#{blockName}"%"
AND block.status = #{status}
AND tag.tag_id IS NULL
AND tag.tag_id = #{tagId}
AND unix_timestamp(block.modified_at) BETWEEN #{modifiedAtFrom} AND #{modifiedAtTo}
