SELECT sku.sku_id, CONVERT(sku.price,DECIMAL(10,2)) AS retail_price, CONVERT(sku.in_price,DECIMAL(10,2)) AS boutique_price, CONVERT(sku.im_price,DECIMAL(10,2)) AS im_price, CONVERT(shop_product_sku.sale_price,DECIMAL(10,2)) AS sale_price, sku.product_id FROM sku LEFT JOIN shop_product_sku ON ( shop_product_sku.sku_id = sku.sku_id AND shop_product_sku.enabled = TRUE ) WHERE (sku.product_id IN
#{item.product_id}
AND sku.enabled = 1) GROUP BY sku.product_id

