SELECT product.product_id, shop_product.shop_product_id, product.status AS product_status, shop_product.status AS shop_product_status, brand.english_name AS brand_name, vendor.vendor_name AS boutique, product.product_code AS boutique_id, product.season_code AS season_code, product.cover_img, product.description_img, product.category_id, product.designer_id AS designer_id, product.color_code AS color_code FROM product LEFT JOIN shop_product ON shop_product.product_id = product.product_id AND shop_product.shop_id = 65 AND shop_product.enabled = TRUE INNER JOIN vendor ON product.vendor_id = vendor.vendor_id AND product.enabled = TRUE INNER JOIN brand ON brand.brand_id = product.brand_id AND brand.enabled = TRUE WHERE
(product.status = 4 OR product.status = 2)
product.status = #{status}
AND shop_product.enabled IS NULL
product.status = #{status} AND shop_product.status = #{shopStatus} AND shop_product.enabled = 1
AND brand.brand_id = #{brandId}
AND product.category_id IN
#{item}
AND product.season_code = #{season}
AND product.product_code = #{boutiqueId}
AND vendor.vendor_id = #{vendorId}
AND (product.cover_img != NULL AND product.description_img != NULL )
AND (product.cover_img == NULL OR product.description_img == NULL )
AND product.img_modified = 1
AND product.img_modified = 0
AND product.designer_id LIKE "%"#{designerId}"%"
AND product.color_code = #{colorCode}
ORDER BY
vendor.vendor_name
product.product_code
product.season_code
contact(product.designer_id, product.color_code)
product.product_id
DESC
LIMIT #{start}, #{count};

SELECT product.product_id, shop_product.shop_product_id, product.status AS product_status, shop_product.status AS shop_product_status FROM product LEFT JOIN shop_product ON product.product_id = shop_product.product_id AND shop_product.shop_id = 65 AND shop_product.enabled = 1 WHERE product.product_id IN
#{item}
AND product.enabled = TRUE ;

SELECT sku.sku_id, CONVERT(sku.price,DECIMAL(10,2)) AS retail_price, CONVERT(sku.in_price,DECIMAL(10,2)) AS boutique_price, CONVERT(sku.im_price,DECIMAL(10,2)) AS im_price, CONVERT(shop_product_sku.sale_price,DECIMAL(10,2)) AS sale_price, sku.product_id FROM sku LEFT JOIN shop_product_sku ON ( shop_product_sku.sku_id = sku.sku_id AND shop_product_sku.enabled = TRUE ) WHERE (sku.product_id IN
#{item.product_id}
AND sku.enabled = 1) GROUP BY sku.product_id

SELECT
product.status AS product_status,
shop_product.status AS shop_product_status,
COUNT(product.status) AS count
FROM product
LEFT JOIN shop_product ON shop_product.product_id = product.product_id AND shop_product.enabled = TRUE

    INNER JOIN brand ON brand.brand_id = product.brand_id AND brand.enabled = TRUE


    INNER JOIN vendor ON product.vendor_id = vendor.vendor_id AND product.enabled = TRUE


WHERE product.enabled = TRUE

    AND brand.brand_id = #{brandId}


    AND product.category_id = #{categoryId}


    AND product.season_code = #{season}


    AND product.product_code = #{boutiqueId}


    AND vendor.vendor_id = #{vendorId}


    AND product.designer_id = #{designerId}


    AND product.color_code = #{colorCode}






        AND (product.cover_img != NULL AND product.description_img != NULL )


        AND (product.cover_img == NULL OR product.description_img == NULL )







        AND product.img_modified = 1


        AND product.img_modified = 0
























GROUP BY product.status, shop_product.status;

