mapper.select:
SELECT product.product_id, shop_product.shop_product_id, product.status AS product_status, shop_product.status AS shop_product_status, brand.english_name AS brand_name, vendor.vendor_name AS boutique, product.product_code AS boutique_id, product.season_code AS season_code, product.category_id,
shop_product_sku.sale_price AS sale_price, shop_product_sku.enabled AS shop_product_sku_enabled,
product.designer_id AS designer_id, product.color_code AS color_code FROM product LEFT JOIN shop_product ON shop_product.product_id = product.product_id AND shop_product.shop_id = 65 INNER JOIN vendor ON product.vendor_id = vendor.vendor_id AND product.enabled = 1 INNER JOIN brand ON brand.brand_id = product.brand_id
INNER JOIN shop_product_sku ON (shop_product_sku.sku_id = t_sku.sku_id AND shop_product_sku.enabled = 1)
WHERE
product.status = 4 OR product.status = 2
product.status = #{status}
AND (shop_product.enabled = 0 OR shop_product.enabled IS NULL)
product.status = #{status} AND shop_product.status = #{shopStatus} AND shop_product.enabled = 1
AND sum_stock.stock > 0
AND sum_stock.stock <= 0
AND brand.english_name = #{brand}
AND product.category_id = #{category}
AND product.season_code = #{season}
AND product.product_code = #{boutiqueid}
AND vendor.vendor_name = #{boutique}
AND (product.cover_img != NULL AND product.description_img != NULL )
AND (product.cover_img == NULL OR product.description_img == NULL )
AND product.img_modified = 1
AND product.img_modified = 0
AND product.designer_id = #{designerid}
AND product.color_code = #{colorcode}
LIMIT #{start}, #{count};
